import { StyleSheet, Text, View, TouchableOpacity, Image } from "react-native";
import * as ImagePicker from "expo-image-picker";
import React, { useState } from "react";

const AddProfilePicture = () => {
  const [image, setImage] = useState(null);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
      base64: true,
    });

    if (!result.cancelled) {
      let base64Img = `data:image/jpg;base64,${result.base64}`;

      // Add your cloud name and upload preset name
      let apiUrl = "https://api.cloudinary.com/v1_1/dzo3dmyye/image/upload";

      let data = {
        file: base64Img,
        upload_preset: "ml_default",
      };

      fetch(apiUrl, {
        body: JSON.stringify(data),
        headers: {
          "content-type": "application/json",
        },
        method: "POST",
      })
        .then(async (response) => {
          let data = await response.json();
          if (data.secure_url) {
            setImage(data.secure_url);
          }
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  const pickImageFromCamera = async () => {
    let result = await ImagePicker.launchCameraAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
      base64: true,
    });

    if (!result.cancelled) {
      let base64Img = `data:image/jpg;base64,${result.base64}`;

      // Add your cloud name and upload preset name
      let apiUrl = "https://api.cloudinary.com/v1_1/dzo3dmyye/image/upload";

      let data = {
        file: base64Img,
        upload_preset: "ml_default",
      };

      fetch(apiUrl, {
        body: JSON.stringify(data),
        headers: {
          "content-type": "application/json",
        },
        method: "POST",
      })
        .then(async (response) => {
          let data = await response.json();
          console.log("====================================");
          console.log(data);
          console.log("====================================");
          if (data.secure_url) {
            setImage(data.secure_url);
          }
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={pickImage}>
        <Text>Add Profile Picture from gallery</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={pickImageFromCamera}>
        <Text>Add Profile Picture from camera</Text>
      </TouchableOpacity>
      {image && <Image source={{ uri: image }} style={styles.image} />}
    </View>
  );
};

export default AddProfilePicture;

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
  },
  image: {
    width: 200,
    height: 200,
    marginTop: 10,
  },
});
