import React from "react";
import { ScrollView } from "react-native";
import styled from "styled-components/native";
import Grid from "../components/grid.component";
import { SafeArea } from "../../../components/utility/safe-area.component";

const Container = styled.View`
  flex: 1;
`;

const Content = styled.View`
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;
  padding: 10px;
`;

const DashboardScreen = () => {
  return (
    <SafeArea>
      <Container>
        <ScrollView>
          <Content>
            <Grid
              delay={100}
              icon="check-circle-outline"
              title="Active Task"
              colorIndex={3}
              myOpacity={0.5}
            />
            <Grid
              delay={200}
              icon="history"
              title="Last Task Review"
              colorIndex={3}
              myOpacity={0.15}
            />
            <Grid
              delay={300}
              icon="tag-heart"
              title="Offers Requests"
              colorIndex={2}
              myOpacity={0.2}
            />
            <Grid
              delay={600}
              icon="star-circle-outline"
              title="Ratings"
              colorIndex={6}
              alternativeColorIndex={7}
              myOpacity={0.4}
            />
            <Grid
              delay={500}
              icon="calendar-clock"
              title="Deadline"
              colorIndex={0}
              myOpacity={0.3}
            />
            <Grid
              delay={400}
              icon="file-chart"
              title="Reports"
              colorIndex={5}
              myOpacity={0.4}
            />
            <Grid
              delay={700}
              icon="eye-circle-outline"
              title="Show History"
              colorIndex={1}
              myOpacity={0.2}
            />
          </Content>
        </ScrollView>
      </Container>
    </SafeArea>
  );
};

export default DashboardScreen;
