import {
  StyleSheet,
  View,
  FlatList,
  TouchableOpacity,
  ScrollView,
} from "react-native";
import React, { useState } from "react";
import styled from "styled-components/native";
import { Text } from "../../../components/typography/text.component";
import { Button } from "react-native-paper";

let tasksRequests = [
  {
    workerName: "John Doe",
    taskTitle: "Task 1",
    taskDate: "2023-05-17",
  },
  {
    workerName: "Jane Smith",
    taskTitle: "Task 2",
    taskDate: "2023-05-18",
  },
  {
    workerName: "Bob Johnson",
    taskTitle: "Tahsk 3",
    taskDate: "2023-05-19",
  },
  {
    workerName: "John Doe",
    taskTitle: "Tassk 1",
    taskDate: "2023-05-17",
  },
  {
    workerName: "Jane Smith",
    taskTitle: "Tassk 2",
    taskDate: "2023-05-18",
  },
  {
    workerName: "Bob Johnson",
    taskTitle: "Tashsk 3",
    taskDate: "2023-05-19",
  },
];
let tasksHistory = [
  {
    workerName: "John Doe",
    taskTitle: "past 1",
    taskDate: "2023-05-17",
  },
  {
    workerName: "Jane Smith",
    taskTitle: "past 2",
    taskDate: "2023-05-18",
  },
  {
    workerName: "Bob Johnson",
    taskTitle: "past 3",
    taskDate: "2023-05-19",
  },
];

const RequestsView = styled.View`
  background-color: #9acdea;
`;
const TaskView = styled.View`
  border-color: red;
  border-width: 1px;
  margin: 5px;
  padding: 16px;
`;

const Subtitle = styled(Text)`
  color: red;
`;

const Divider = styled.View`
  height: 1px;
  width: 100%;
  background-color: #d3d3d3;
`;

const TaskRequest = () => {
  const [tasksHistorySelected, setTasksHistorySelected] = useState(false);

  return (
    <RequestsView>
      <View style={{ flexDirection: "row", justifyContent: "center" }}>
        <Button
          mode="contained"
          onPress={() => setTasksHistorySelected(false)}
          style={{
            opacity: tasksHistorySelected ? 0.5 : 1,
            width: "45%",
            borderTopRightRadius: 0,
            borderBottomRightRadius: 0,
          }}
        >
          about me
        </Button>
        <Button
          mode="contained"
          onPress={() => setTasksHistorySelected(true)}
          style={{
            opacity: tasksHistorySelected ? 1 : 0.5,
            width: "45%",
            borderTopLeftRadius: 0,
            borderBottomLeftRadius: 0,
          }}
        >
          portfolio
        </Button>
      </View>

      {!tasksHistorySelected && <Text>about me</Text>}

      {tasksHistorySelected && <Text>portfolio</Text>}
    </RequestsView>
  );
};

export default TaskRequest;

const styles = StyleSheet.create({});
