import { StyleSheet, Text, View } from "react-native";
import React, { useState, useContext, useLayoutEffect } from "react";
import { ScrollView } from "react-native";
import { List, Divider, MD3Colors } from "react-native-paper";

import { Spacer } from "../../../components/spacer/spacer.component";

import { SafeArea } from "../../../components/utility/safe-area.component";
import { WorkerCard } from "../../homepage/components/worker-card.component";
import { ReqButton } from "../components/worker-profile.style";

const WorkerProfile = ({ route, navigation }) => {
  useLayoutEffect(() => {
    navigation.setOptions({
      title: route.params.worker.workerFirstName + " Profile",
    });
  }, [navigation, route.params.worker.workerFirstName]);

  const [SummaryExpanded, setSummaryExpanded] = useState(false);
  const [BiographieExpanded, setBiographieExpanded] = useState(false);

  return (
    <SafeArea>
      <ScrollView>
        <Spacer position="top" size="medium" />

        <WorkerCard />
        <Spacer position="top" size="large" />
        <List.Accordion
          title="Professional Summary"
          left={(props) => <List.Icon {...props} icon="bookshelf" />}
          expanded={SummaryExpanded}
          onPress={() => setSummaryExpanded(!SummaryExpanded)}
        >
          <Text>{route.params.worker.workerProfessionalSummary}</Text>
        </List.Accordion>
        <List.Accordion
          title="Biographie"
          left={(props) => <List.Icon {...props} icon="feather" />}
          expanded={BiographieExpanded}
          onPress={() => setBiographieExpanded(!BiographieExpanded)}
        >
          <Text>{route.params.worker.workerBio}</Text>
        </List.Accordion>
        <ReqButton
          icon="handshake"
          mode="contained"
          onPress={() => console.log("Button pressed")}
        >
          Request Service
        </ReqButton>
      </ScrollView>
    </SafeArea>
  );
};

export default WorkerProfile;

const styles = StyleSheet.create({});
