import React from "react";
import { SvgXml } from "react-native-svg";
import { View } from "react-native";

import { Spacer } from "../../../components/spacer/spacer.component";
import { Text } from "../../../components/typography/text.component";
import star from "../../../../assets/star";
import open from "../../../../assets/open";
import { PHOTOS } from "../mockdata/workers";

import {
  WorkerrCard,
  WorkerPhoto,
  Info,
  Section,
  SectionEnd,
  Rating,
  Icon,
  Address,
  WorkerPhotoWrapper,
  CardTitle,
} from "./worker-card.styles";

export const WorkerCard = ({worker={
  workerFirstName: 'Mike',
  workerLastName: 'Smith',
  workerAddress: '789 Oak St',
  workerEmail: 'mikesmith@example.com',
  workerCategory: 'Landscaper',
  workerDateOfBirth: '1990-05-25',
  workerYearsOfExperience: 3,
  workerTotalRates: 8,
  workerPhoneNumber: '555-555-5555',
  workerNumberOfJobs: 15,
  workerAvailabillity: 0,
  workerPassword: 'password123',
  workerJob: 'Landscaper',
  workerProfessionalSummary: 'I have experience in both residential and commercial landscaping.',
  workerTotalRating: 7,
  workerBio: 'In my free time, I like to go fishing and spend time with my family.'
}}) => {
  const rating = worker.workerTotalRating/ worker.workerTotalRates;
  const ratingArray = Array.from(new Array(
    Math.round(rating)
  ));
  return (
    <WorkerrCard elevation={2}>
      <Spacer size="medium" />
      <CardTitle variant="hint">{worker.workerJob}</CardTitle>
      <WorkerPhotoWrapper>
        <WorkerPhoto
          source={{
            uri: PHOTOS[Math.floor(Math.random()*PHOTOS.length)],
          }}
        />
      </WorkerPhotoWrapper>
      <Info>
        <Section>
          <Text variant="caption">Name: </Text>
          <Text variant="label">{worker.workerFirstName}</Text>
          <SectionEnd>
            <Text>{rating}</Text>
            {ratingArray.map((_, i) => (
              <SvgXml key={i} xml={star} width={20} height={20} />
            ))}
          </SectionEnd>
        </Section>
        <Spacer />
        <Section>
          <Text variant="caption">Address: </Text>
          <Address>{worker.workerAddress}</Address>
          <SectionEnd>
            {true && <Text variant="success">AVAILABLE</Text>}
          </SectionEnd>
        </Section>
      </Info>
    </WorkerrCard>
  );
};
