import styled from "styled-components/native";
import { Card } from "react-native-paper";
import { View } from "react-native";
import { Text } from "../../../components/typography/text.component";

export const WorkerrCard = styled(Card)`
  background-color: ${(props) => props.theme.colors.blue.third};
  width: 95%;
  align-self: center;
  margin-top: 0;

`;

export const CardTitle = styled(Text)`
  text-align: center;
  font-size: ${(props) => props.theme.fontSizes.title};
`;

export const WorkerPhotoWrapper = styled(View)`
  margin: ${(props) => props.theme.space[1]};
`;

export const WorkerPhoto = styled(Card.Cover)`
  padding: ${(props) => props.theme.space[0]};
  background-color: ${(props) => props.theme.colors.blue.first};
`;

export const Address = styled.Text`
  font-family: ${(props) => props.theme.fonts.body};
  font-size: ${(props) => props.theme.fontSizes.caption};
`;

export const Info = styled.View`
  padding: ${(props) => props.theme.space[1]};
`;

export const Section = styled.View`
  flex-direction: row;
  align-items: center;
`;

export const SectionEnd = styled.View`
  flex: 1;
  flex-direction: row;
  justify-content: flex-end;
`;
